settings.custom_css:
selector .elementor-container{
    align-content: flex-end;
}
selector>.elementor-background-overlay {
    background: rgb(53,56,57);
    background: -moz-linear-gradient(0deg, rgba(53,56,57,1) 0%, rgba(255,255,255,0) 50%, rgba(255,255,255,0) 75%, rgba(53,56,57,1) 100%);
    background: -webkit-linear-gradient(0deg, rgba(53,56,57,1) 0%, rgba(255,255,255,0) 50%, rgba(255,255,255,0) 75%, rgba(53,56,57,1) 100%);
    background: linear-gradient(0deg, rgba(53,56,57,1) 0%, rgba(255,255,255,0) 50%, rgba(255,255,255,0) 75%, rgba(53,56,57,1) 100%);
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#353839",endColorstr="#353839",GradientType=1);
}

selector .date_panel,
selector .stock,
selector .yith_evti_total_price .price{
    display: none;
}

selector .quantity.buttons_added {
    width: 100% !important;
    display: flex !important;
    flex-direction: row;
    flex-wrap: nowrap;
    align-content: center;
    align-items: center;
    column-gap: 8px;
    margin: 16px 0px !important;
}

selector .quantity.buttons_added input.qty {
    float: none;
    height: 40px;
    flex: 1 1 40px !important;
    margin: 0px;
    border-radius: 8px;
}

selector .quantity.buttons_added input.button {
    background-color: #383838 !important;
    color: #fff;
    width: 40px;
    float: none;
    flex: 0 1 auto !important;
    border: solid lightgrey  1px !important;
    border-radius: 8px !important;

}

selector .field_service_row
{
    padding: 16px;
    font-size: 14px;
    margin-bottom: 16px;
    border-radius: 8px;
}

selector .field_service_row .field_row .form-field
{
    margin-bottom: 4px;
}

selector .field_service_row .field_row .form-field label
{
    margin-bottom: 4px;
}
